import { useEffect, useState } from "react";
import axios from "axios";

const apkpost = "http://localhost:5000/api/item/post";
const apkget = "http://localhost:5000/api/item/get";
const apkput ="localhost:500/api/item/:id";

function App() {
  const [items, setItem] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");

  useEffect(() => {
    fechitem();
  }, []);

  const fechitem = async () => {
    try {
      const res = await axios.get(apkget);
      setItem(res.data);
    } catch (err) {
      console.error("Error fetching data:");
    }
  };

  const creactitem = async () => {
    try {
      await axios.post(apkpost, { name, phone });
      setName("");
      setPhone("");
      fechitem();
    } catch (err) {
      console.error("Error posting data:");
    }
  };



  const update = async(id)=>{
    try{
      const newupdata = await axios.put(apkput,{name,phone});
      setName("");
      setPhone("")
      fechitem();


    }catch(err){
      console.error

    }


  }

  return (
    <div>
      <h1>Contact List</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          creactitem();
        }}
      >
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <br />
        <br />
        <input
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          placeholder="Phone"
          required
        />
        <br />
        <br />
        <button type="submit">Add to List</button>
      </form>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> {item.phone}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
