import { useEffect, useState } from "react";
import axios from "axios";

const apkpost = "http://localhost:5000/api/item/post";
const apkget = "http://localhost:5000/api/item/get";
const apkput = "http://localhost:5000/api/item";

function App() {
  const [items, setItem] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");

  useEffect(() => {
    fechitem();
  }, []);

  const fechitem = async () => {
    try {
      const res = await axios.get(apkget);
      setItem(res.data);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  const creactitem = async () => {
    try {
      await axios.post(apkpost, { name, phone });
      setName("");
      setPhone("");
      fechitem();
    } catch (err) {
      console.error("Error posting data:", err);
    }
  };

  const update = async (id) => {
    try {
      await axios.put('${apkput}/${id}', { name, phone }); // Corrected
      setName("");
      setPhone("");
      fechitem();
    } catch (err) {
      console.error("Error updating data:", err);
    }
  };

  return (
    <div>
      <h1>Contact List</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          creactitem();
        }}
      >
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <br />
        <br />
        <input
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          placeholder="Phone"
          required
        />
        <br />
        <br />
        <button type="submit">Add to List</button>
      </form>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> {item.phone}
            <button onClick={() => update(item._id)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
