import { useEffect, useState } from "react";
import axios from "axios";

const apiBase = "http://localhost:5000/api/item";

function App() {
  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get(`${apiBase}/get`);
      setItems(res.data);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  const createItem = async () => {
    try {
      await axios.post(`${apiBase}/post`, { name, phone });
      setName("");
      setPhone("");
      fetchItems();
    } catch (err) {
      console.error("Error posting data:", err);
    }
  };

  const updateItem = async (id) => {
    try {
      await axios.put(`${apiBase}/${id}`, { name, phone });
      setEditingId(null);
      setName("");
      setPhone("");
      fetchItems();
    } catch (err) {
      console.error("Error updating data:", err);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(`${apiBase}/${id}`);
      fetchItems();
    } catch (err) {
      console.error("Error deleting data:", err);
    }
  };

  const startEditing = (item) => {
    setEditingId(item._id);
    setName(item.name);
    setPhone(item.phone);
  };

  return (
    <div>
      <h1>Contact List</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          editingId ? updateItem(editingId) : createItem();
        }}
      >
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <br />
        <br />
        <input
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          placeholder="Phone"
          required
        />
        <br />
        <br />
        <button type="submit">{editingId ? "Update" : "Add to List"}</button>
      </form>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> {item.phone}
            <button onClick={() => startEditing(item)}>Edit</button>
            <button onClick={() => deleteItem(item._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
